import React from 'react';
import './styles.css';
import './w3.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import {NavLink} from 'react-router-dom'



function myFunction() {
  document.getElementById("myBtn").disabled = true;
}



function Home(){
	return (
    <div>
      <div className="header">
        <h1>Community Charities</h1>
        <i> - Bringing Together Local Charities, Local Businesses, and Local Community - </i>
      </div>

      <div className="w3-container">
        <div className="w3-bar w3-border w3-card-4 w3-teal">
          <NavLink exact activeClassName="active" to = '/about' >About</NavLink>>
          <a href="#" className="w3-bar-item w3-button w3-hover-black w3-text-white w3-hover-text-white w3-mobile">Charity Partners</a>
          <a href="#" className="w3-bar-item w3-button w3-hover-black w3-text-white w3-hover-text-white w3-mobile">Business Partners</a>
          <a href="#" className="w3-bar-item w3-button w3-hover-black w3-text-white w3-hover-text-white w3-mobile">Charity Events</a>
        </div>
      </div>

      <div className="row">
        <div className="column" style={{backgroundColor: "rgb(253, 120, 103)"}}>
          <h2>Local Charities Sign-up Here</h2>
          <p>Some text..</p>
          <button id="myBtn" onClick={myFunction}>Create Account</button>
        </div>
        <div className="column" style={{backgroundColor: "rgb(248, 220, 220)"}}>
          <h2>Local Businesses Sign-up Here</h2>
          <p>hhhh</p>
          <button id="myBtn" onClick={myFunction}>Create Account</button>
        </div>
        <div className="column" style={{backgroundColor: "#ffff"}}>
          <h2>Individuals Sign-up Here</h2>
          <p>Some text..</p>
          <button id="myBtn" onClick={myFunction}>Create Account</button>
        </div>
      </div>
    </div>
  );


}

function About() {
  return (
    <div>
      <h1>About Page</h1>
      <Link to="/">Go to Home Page</Link>
    </div>
  );
}


function App() {
    return (
    <Router>
      <Routes>
        <Route exact path="/" element={<Home/>} />
        <Route path="/about" element={<About/>} />
      </Routes>
    </Router>
  );


}

export default App;
